import React, { Component } from "react";
import Header from "./Header";
import Score from "./score/Score";
import GameOption from "./gameOptions/GameOption";
import Rock from "./gameOptions/Rock";
import Paper from "./gameOptions/Paper";
import Scissors from "./gameOptions/Scissors";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";

export default class Game extends Component {
  state = {
    humanScore: 0,
    computerScore: 0,
    rock: {
      id: 0,
      name: "rock"
    },
    paper: {
      id: 1,
      name: "paper"
    },
    scissors: {
      id: 2,
      name: "scissors"
    }
  };

  getComputerChoice = () => {
    return Math.floor(Math.random() * 3);
  };

  handleSelection = (selection, e) => {
    const computerChoice = this.getComputerChoice();

    if (selection === computerChoice) {
      alert("You are tied.");
    }

    switch (selection) {
      case 0:
        console.log("rock");
        if (computerChoice == 1) {
          alert("You lost.");
          this.setState({ computerScore: this.state.computerScore + 1 });
        } else if (computerChoice == 2) {
          alert("You won.");
          this.setState({ humanScore: this.state.humanScore + 1 });
        }
        break;
      case 1:
        console.log("paper");
        if (computerChoice == 2) {
          alert("You lost.");
          this.setState({ computerScore: this.state.computerScore + 1 });
        } else if (computerChoice == 0) {
          alert("You won.");
          this.setState({ humanScore: this.state.humanScore + 1 });
        }
        break;
        break;
      case 2:
        console.log("scissor");
        if (computerChoice == 0) {
          alert("You lost.");
          this.setState({ computerScore: this.state.computerScore + 1 });
        } else if (computerChoice == 1) {
          alert("You won.");
          this.setState({ humanScore: this.state.humanScore + 1 });
        }
        break;
        break;
    }
  };

  render() {
    const state = this.state;
    return (
      <div className="wrapper">
        <Header></Header>

        <Container style={{ padding: "1em 0" }}>
          <Score
            humanScore={state.humanScore}
            computerScore={state.computerScore}
          ></Score>

          <Row>
            <GameOption
              onSelection={this.handleSelection}
              option={this.state.rock}
            ></GameOption>
            <GameOption
              onSelection={this.handleSelection}
              option={this.state.paper}
            ></GameOption>
            <GameOption
              onSelection={this.handleSelection}
              option={this.state.scissors}
            ></GameOption>
          </Row>
        </Container>
      </div>
    );
  }
}
