import React, { Component } from "react";
import Header from "./Header";
import Score from "./score/Score";
import GameOption from "./gameOptions/GameOption";
import Rock from "./gameOptions/Rock";
import Paper from "./gameOptions/Paper";
import Scissors from "./gameOptions/Scissors";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";

export default class Game extends Component {
  state = {
    humanScore: 0,
    computerScore: 0,
    rock: {
      id: 0,
      name: "rock"
    },
    paper: {
      id: 1,
      name: "paper"
    },
    scissor: {
      id: 2,
      name: "scissor"
    }
  };

  getComputerChoice = () => {
    return Math.floor(Math.random() * 3);
  };

  handleSelection = selection => {
    switch (selection) {
      case 0:
        console.log("rock");
        break;
      case 1:
        console.log("paper");
        break;
      case 2:
        console.log("scissor");
        break;
    }
  };

  render() {
    const state = this.state;
    console.log(state);
    return (
      <div className="wrapper">
        <Header></Header>

        <Container style={{ padding: "1em 0" }}>
          <Score
            humanScore={state.humanScore}
            computerScore={state.computerScore}
          ></Score>

          <Row>
            <GameOption onSelection={this.handleSelection}></GameOption>
          </Row>
        </Container>
      </div>
    );
  }
}
