import React, { Component } from "react";
import Header from "./Header";
import Score from "./score/Score";
import GameOptions from "./gameOptions/GameOptions";
import Rock from "./gameOptions/Rock";
import Paper from "./gameOptions/Paper";
import Scissors from "./gameOptions/Scissors";
import Container from "react-bootstrap/Container";

export default class Game extends Component {
  state = {
    humanScore: 0,
    computerScore: 0
  };

  render() {
    const state = this.state;
    console.log(state);
    return (
      <div className="wrapper">
        <Header></Header>
        <Container>
          <Score humanScore={state.humanScore}></Score>

          {/* <GameOptions>
          <Rock></Rock>

          <Paper></Paper>

          <Scissors></Scissors>
        </GameOptions> */}
        </Container>
      </div>
    );
  }
}
